import { useEffect, useState } from "react";
import { Decal, useGLTF, useTexture } from "@react-three/drei";
import { emitter } from "@/services/mitt";
import { colors } from "./three-controls/colors";
import * as THREE from "three";

const defaultImageBackground =
  "data:image/png;base64,";

export function Model() {
  const { nodes, materials } = useGLTF("/tee.glb");

  const [texture, setTexture] = useState(defaultImageBackground);
  const textureOnShirt = useTexture(texture);

  const [color, setColor] = useState({
    color: colors[1].color,
    taglessColor: colors[1].taglessColor,
  });

  useEffect(() => {
    emitter.on("teeColor", (color) => {
      setColor(color);
    });

    emitter.on("updateTexture", (texture) => {
      console.log("ðŸš€ ~ emitter.on ~ texture:", texture);
      setTexture(texture);
    });

    return () => {
      emitter.off("teeColor");
      emitter.off("updateTexture");
    };
  }, []);

  return (
    <group dispose={null}>
      <mesh
        castShadow
        receiveShadow
        geometry={(nodes.Camisa as any).geometry}
        material={materials["Material.003"]}
        position={[0.101, 0, -0.09]}
        rotation={[Math.PI / 2, 0, -0.146]}
        material-color={color.color}
      >
        <Decal
          // rotation={[0, 0, 0]} // Ensure this rotation is appropriate for the decal
          rotation={[-5, 9.4, 9.41]}
          position={[0, 5, -40]}
          scale={30}
        >
          <meshPhysicalMaterial
            map={textureOnShirt}
            transparent
            side={THREE.FrontSide}
          />
        </Decal>
      </mesh>

      <mesh
        castShadow
        geometry={(nodes.Costas as any).geometry}
        material={materials["Material.002"]}
        position={[0.101, 0, -0.1]}
        rotation={[Math.PI / 2, 0, -0.146]}
        material-color={color.color}
      >
        {/* <Decal
          // rotation={[0, 0, 0]} // Ensure this rotation is appropriate for the decal
          rotation={[-4.7, 12.5, 9.4]}
          position={[0, 5, -56]}
          scale={45}
        >
          <meshPhysicalMaterial
            map={textureOnShirt}
            transparent
            side={THREE.FrontSide}
          />
        </Decal> */}
      </mesh>

      <mesh
        castShadow
        receiveShadow
        geometry={(nodes.Curve as any).geometry}
        material={materials["SVGMat.002"]}
        position={[-2.238, 56.022, -1.386]}
        rotation={[Math.PI / 2, 0, -0.021]}
        scale={15.928}
        material-color={color.taglessColor}
      />
    </group>
  );
}

useGLTF.preload("/tee.glb");
